(function() {
  var File;

  File = function(I) {
    var self;
    if (I == null) {
      I = {};
    }
    Object.defaults(I, {
      content: ""
    });
    if (!I.path) {
      throw "File must have a path";
    }
    self = Model(I).observeAll();
    self.extend({
      extension: function() {
        return self.path().extension();
      },
      mode: function() {
        var extension;
        switch (extension = self.extension()) {
          case "js":
            return "javascript";
          case "md":
            return "markdown";
          case "cson":
            return "coffee";
          case "":
            return "text";
          default:
            return extension;
        }
      },
      modified: Observable(false),
      displayName: Observable(self.path())
    });
    self.content.observe(function() {
      return self.modified(true);
    });
    self.modified.observe(function(modified) {
      if (modified) {
        return self.displayName("*" + (self.path()));
      } else {
        return self.displayName(self.path());
      }
    });
    return self;
  };

  module.exports = File;

}).call(this);

